class QueryCache {
  async get(sql, tables) {
    const key = hashQuery(sql, tables);
    return redis.get(key);
  }

  async set(sql, tables, result) {
    const key = hashQuery(sql, tables);
    await redis.setex(key, 3600, JSON.stringify(result));
  }
}
