```typescript
export class PerformanceMonitor {
  private metrics: Map<string, Metric[]> = new Map();

  startTiming(operation: string): Timer {
    const startTime = performance.now();
    return {
      end: (metadata?: Record<string, any>) => {
        const duration = performance.now() - startTime;
        this.recordMetric({
          operation,
          duration,
          timestamp: Date.now(),
          ...metadata,
        });
      }
    };
  }

  recordMetric(metric: Metric): void {
    const key = metric.operation;
    if (!this.metrics.has(key)) {
      this.metrics.set(key, []);
    }
    this.metrics.get(key)!.push(metric);

    // Log to console in development
    if (process.env.NODE_ENV === 'development') {
      console.log(`[PERF] ${metric.operation}: ${metric.duration.toFixed(2)}ms`);
    }
  }

  getStats(operation: string): Stats {
    const metrics = this.metrics.get(operation) || [];
    return {
      count: metrics.length,
      avg: average(metrics.map(m => m.duration)),
      p50: percentile(metrics, 50),
      p95: percentile(metrics, 95),
      p99: percentile(metrics, 99),
    };
  }
}
```

